package Practise;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Primitive data types: int, double, char, boolean
		//1. Interger
		int i =20;
		int j = 10;
		int sum = i+j;
		System.out.println("sum of i and j = "+ sum   );
		//2. Double
		double d= 12.33;
		double e= 33.44;
		//3.Character only single digit value. and should be written within single quotes;
		char c = 'o';
		char g= 'A';
		
		//4. Boolean; True and False
		boolean b1= true;
		boolean b2= false;
		
		//5.String: String is a not a data type, it is class, but it is used as data type
		String s= "Happy to Learn";
		String s1= "Selenium";
		String s2= "I have to Master in Java and Selenium Automation";
		String s3= "1000";

			
		
		System.out.println("Hello world");

	}

}
